export function eolMetadata() {
    return (tree) => {
        for (let i = 0; i < tree.children.length; i++) {
            // const node = tree.children[i];
            const next = tree.children[i + 1];
            if (next && next.type === "text" && next.value.startsWith("\n")) {
                next.data = next.data || {};
                next.data.eol = true;
            }
        }
    };
}
